import { Routes, Route, useNavigate } from 'react-router-dom';
import React, { useState } from 'react';

import Navbar from './Navbar';
import Home from './Home';
import Meme from './Meme';
import Signup from './Signup';
import Addpost from './Addpost';
import Customisedmeme from './Customisedmeme';


function App() {
  const [setIsAuthenticated,isAuthenticated,] = useState(false);
  const [Memes, setMyMemes] = useState([])
  const [memes, setMemes] = useState([])

  const navigate = useNavigate();

  const handleLogout = (e) => {
    e.preventDefault();
    setIsAuthenticated(false);
    navigate('/');
  };
   // fetch 
   useEffect(() => {
    fetch("http://localhost:9292/memes")
      .then((r) => r.json())
      .then((Memes) => setMemes(Memes));
  }, []);

//   fetch memes for user
  useEffect(() => {
    fetch("http://localhost:9292/users/1")
      .then((r) => r.json())
      .then((myMemes) => setMyMemes(myMemes));
  }, []);

  function handleDeleteMessage(id) {
    const updatedmemes = Memes.filter((myMemes) => myMemes.id !== id);
    console.log("update delete message")
    setMyMemes(updatedmemes);
  }

  return (
    <div className="App">
      <Navbar isAuthenticated={isAuthenticated} handleLogout={handleLogout} />
      <Routes>
        <Route path="/" element={<Home setIsAuthenticated={setIsAuthenticated} />} />
        <Route path="/signup" element={<Signup setIsAuthenticated={setIsAuthenticated} />} />
        {isAuthenticated && (
          <Route path="/shelf" element={<Meme />} />
        )}
      </Routes>
    </div>
  );
}
export default App;
