import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Signin from "./Signin";
import Signup from "./Signup";
import Meme from "./Meme";
import "./style.css";

const Home = ({ isAuthenticated, setIsAuthenticated, myMemes }) => {
  const [showSignup, setShowSignup] = useState(false);
  const navigate = useNavigate();

  const handleSigninSubmit = (e) => {
    e.preventDefault();
    navigate("/allmemes");
    setIsAuthenticated(true);
  };

  return (
    <div className="box-container">
      <div className="bg-cover bg-center flex flex-col justify-center items-center text-white">
        <Signin handleSubmit={handleSigninSubmit} />
      </div>
      {showSignup && (
        <div className="bg-cover bg-center flex flex-col justify-center items-center text-white">
          <Signup setIsAuthenticated={setIsAuthenticated} />
        </div>
      )}
      {isAuthenticated && (
        <div className="meme-container">
          <Meme />
        </div>
      )}
      {myMemes && (
        <div className="meme-container">
          {myMemes.map((meme) => (
            <MemeGenerator key={meme.id} memes={[meme]} />
          ))}
        </div>
      )}
    </div>
  );
};

export default Home;
