import { useState, useEffect } from "react";

const Home = ({ isAuthenticated, handleLogin }) => {
  const [meme, setMeme] = useState(null);

  useEffect(() => {
    fetch("https://api.imgflip.com/get_memes")
      .then(response => response.json())
      .then(data => {
        const memes = data.data.memes;
        const randomIndex = Math.floor(Math.random() * memes.length);
        const randomMeme = memes[randomIndex];
        setMeme(randomMeme);
      })
      .catch(error => console.log(error));
  }, []);

  return (
    <div>
      {isAuthenticated ? (
        <p>Generate memes like a pro with our easy-to-use tool!</p>
      ) : (
        <div className="bg-cover bg-center flex flex-col justify-center items-center text-white">
          {meme ? (
            <div className="flex flex-col justify-center items-center">
              <img src={meme.url} alt={meme.name} className="max-w-md my-5" />
              <p>{meme.name}</p>
            </div>
          ) : (
            <p>Loading meme...</p>
          )}
          <Signin handleLogin={handleLogin} />
        </div>
      )}
    </div>
  );
};

export default Home;
