import { formatDistance } from "date-fns";
import Search from "./Search";

const MemesList = ({ memes }) => {
  const Memes = memes.map((meme) => {
    const created = new Date(meme.created_at);
    const formattedDate = formatDistance(created, new Date(), {
      addSuffix: true,
    });

    return (
      <div
        key={meme.id}
        className="p-6 mb-6 bg-white rounded-lg shadow-md sm:p-10 lg:p-6"
      >
        <div className="flex items-center justify-between mb-2">
          {meme.user && (
            <p className="text-lg font-medium text-gray-700">
              {meme.user.username}
            </p>
          )}
          <p className="text-sm text-black-600">{formattedDate}</p>
        </div>
        <p className="text-lg font-medium text-black-900 mb-2">
          {meme.title}
        </p>
        <p className="text-black-700">{meme.message}</p>
      </div>
    );
  });

  return <div className="container mx-auto">{Memes}</div>;
};

const Memes = ({ memes, handleSearchChange }) => {
  return (
    <div>
      <Search handleSearchChange={handleSearchChange} />
      <MemesList memes={memes} />
    </div>
  );
};

export default Memes;
