import React, { useState } from 'react';

function MemeGenerator({ memes }) {
  const [selectedMeme, setSelectedMeme] = useState(null);
  const [text, setText] = useState('');
  const [generatedMeme, setGeneratedMeme] = useState(null);

  const handleMemeSelect = (event) => {
    const id = event.target.value;
    const meme = memes.find((meme) => meme.id === id);
    setSelectedMeme(meme);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const params = new URLSearchParams({
      template_id: selectedMeme.id,
      text0: text,
      text1: text,
      username: 'vickyprinz',
      password: '0794262715',
    });

    const response = await fetch(`https://api.imgflip.com/caption_image?${params}`);
    const data = await response.json();

    if (data.success) {
      setGeneratedMeme(data.data.url);
    }
  };

  return (
    <div>
      <h2>Meme Generator</h2>
      <form onSubmit={handleSubmit}>
        <select onChange={handleMemeSelect}>
          <option value="">Select a meme</option>
          {memes.map((meme) => (
            <option key={meme.id} value={meme.id}>
              {meme.name}
            </option>
          ))}
        </select>
        <input
          type="text"
          placeholder="Enter top text"
          value={text}
          onChange={(event) => setText(event.target.value)}
        />
        <button type="submit" disabled={!selectedMeme}>
          Generate Meme
        </button>
      </form>
      {generatedMeme && (
        <div>
          <img src={generatedMeme} alt="Generated Meme" />
        </div>
      )}
    </div>
  );
}

export default MemeGenerator;
